package no.trank.openpipe.solr.analysis.io;

import java.io.IOException;
import java.io.StringReader;
import java.nio.charset.Charset;

import junit.framework.TestCase;

/**
 * @version $Revision$
 */
public class Base64InputStreamTest extends TestCase {
   public static final String RES1 = "Man is distinguished, not only by his reason, but by this singular passion from other animals, which is a lust of the mind, that by a perseverance of delight in the continued and indefatigable generation of knowledge, exceeds the short vehemence of any carnal pleasure.";
   public static final String TEXT1 = "";
   public static final String RES2 = RES1.substring(0, RES1.length() - 1);
   public static final String TEXT2 = "";
   public static final String RES3 = RES1.substring(0, RES1.length() - 2);
   public static final String TEXT3 = "";
   public static final String RES4 = RES1.substring(0, RES1.length() - 3);
   public static final String TEXT4 = "";
   private static final Charset CHARSET = Charset.forName("UTF-8");

   public void testRead() throws Exception {
      test(TEXT1, RES1);
      test(TEXT2, RES2);
      test(TEXT3, RES3);
      test(TEXT4, RES4);
   }

   private static void test(String text, String result) throws IOException {
      final Base64InputStream in = new Base64InputStream(new StringReader(text));
      final byte[] expected = result.getBytes(CHARSET);
      final byte[] bytes = new byte[expected.length];
      final int len = in.read(bytes);
      assertEquals(expected.length, len);
      for (int i = 0; i < len; i++) {
         assertEquals("At index [" + i + ']', expected[i], bytes[i]);
      }
   }
}